class Canastilla extends BaseCanastilla
{
    
     
     public function __toString() {
        return $this->getNumeroSerie();
    }
    
    
          
    public function getCanastillaes(){
        $q = Doctrine_Query::create()
            -> from('Canastilla c')
            ->orderBy('c.id ASC');
       return $q->execute();
    }

    
     public function getCanastillaSelect(){
        $q = Doctrine_Query::create()
            -> from('Canastilla c')
            ->orderBy('c.id ASC');
       return $q;
    }
    
