class ExcursionInclusion extends BaseExcursionInclusion
{
    
    public function __toString() {
        return $this->getNombre();
    }

    public function getExcursionInclusiones() {
        $q = Doctrine_Query::create()
                ->from('ExcursionInclusion c');
        $q->orderBy('c.nombre ASC');
        return $q->execute();
    }

    static public $status = array(
        0 => 'Inactivo',
        1 => 'Activo',
    );

    public function getExcursionInclusionSelectFront() {
        $q = Doctrine_Query::create()
                ->from('ExcursionInclusion c')
                ->orderBy('c.nombre ASC');
        return $q;
    }

    public function postSave($values) {

        $excursion_inclusiones = new ExcursionInclusion();
        $excursion_inclusiones = $excursion_inclusiones->getExcursionInclusiones();
        foreach ($excursion_inclusiones as $excursion_inclusion) {
            sfContext::getInstance()->getUser()->setAttribute('excursiones_inclusiones' . $excursion_inclusion->getId(), $excursion_inclusion->getNombre());
        }
    }

}
