class ExcursionPolitica extends BaseExcursionPolitica
{
    
    public function __toString() {
        return $this->getNombre();
    }

    public function getExcursionPoliticas() {
        $q = Doctrine_Query::create()
                ->from('ExcursionPolitica c');
        $q->orderBy('c.nombre ASC');
        return $q->execute();
    }

    static public $status = array(
        0 => 'Inactivo',
        1 => 'Activo',
    );

    public function getExcursionPoliticaSelectFront() {
        $q = Doctrine_Query::create()
                ->from('ExcursionPolitica c')
                ->orderBy('c.nombre ASC');
        return $q;
    }

    public function postSave($values) {

        $excursion_politicas = new ExcursionPolitica();
        $excursion_politicas = $excursion_politicas->getExcursionPoliticas();
        foreach ($excursion_politicas as $excursion_politica) {
            sfContext::getInstance()->getUser()->setAttribute('excursiones_politicas' . $excursion_politica->getId(), $excursion_politica->getNombre());
        }
    }

}
