class ExcursionRequisito extends BaseExcursionRequisito
{
    
    public function __toString() {
        return $this->getNombre();
    }

    public function getExcursionRequisitos() {
        $q = Doctrine_Query::create()
                ->from('ExcursionRequisito c');
        $q->orderBy('c.nombre ASC');
        return $q->execute();
    }

    static public $status = array(
        0 => 'Inactivo',
        1 => 'Activo',
    );

    public function getExcursionRequisitoSelectFront() {
        $q = Doctrine_Query::create()
                ->from('ExcursionRequisito c')
                ->orderBy('c.nombre ASC');
        return $q;
    }

    public function postSave($values) {

        $excursion_requisitos = new ExcursionRequisito();
        $excursion_requisitos = $excursion_requisitos->getExcursionRequisitos();
        foreach ($excursion_requisitos as $excursion_requisito) {
            sfContext::getInstance()->getUser()->setAttribute('excursiones_requisitos' . $excursion_requisito->getId(), $excursion_requisito->getNombre());
        }
    }

}
