class ProvinciaFinal extends BaseProvincia
{
    public function __toString() {
        return $this->getNombre();
    }

    
    public function getProvincias(){
        $q = Doctrine_Query::create()
            -> from('Provincia p')
            ->orderBy('p.nombre ASC');
       return $q->execute();
    }
    
    public function getProvinciasSelect(){
        $q = Doctrine_Query::create()
            -> from('Provincia p')
            ->orderBy('p.nombre ASC');
       return $q;
    }
    
    
    public function postDelete($values){
        // Elimino las fotos de la carpeta
       if($this->getImagen()){
            unlink(sfConfig::get('sf_upload_dir').'/provincia/'.$this->getImagen());
       }
    }
    
    
}
