class Rede extends BaseRede
{
    
    
     public function __toString() {
        return $this->getNombre();
    }
    
    
          
    public function getRedes(){
        $q = Doctrine_Query::create()
            -> from('Rede c')
            ->orderBy('c.nombre ASC');
       return $q->execute();
    }
    
     public function getRedesSelect(){
        $q = Doctrine_Query::create()
            -> from('Rede c')
            ->where('c.status = 1')
            ->orderBy('c.nombre ASC');
       return $q;
    }
    
    
     public function postSave($values){
   
         $rede = new Rede();
           $redes = $rede->getRedesSelect()->execute();
           foreach ($redes as $red){
               sfContext::getInstance()->getUser()->setAttribute('red'.$red->getId(),$red->getNombre());
               sfContext::getInstance()->getUser()->setAttribute('red_imagen'.$red->getId(),$red->getLogo());
           }
         
         
    }   
    
    
}
