class SeguroBeneficio extends BaseSeguroBeneficio
{
    public function __toString() {
        return $this->getNombre();
    }

    public function getSeguroBeneficios() {
        $q = Doctrine_Query::create()
                ->from('SeguroBeneficio c');
        $q->orderBy('c.nombre ASC');
        return $q->execute();
    }

    static public $status = array(
        0 => 'Inactivo',
        1 => 'Activo',
    );

    public function getSeguroBeneficioSelectFront() {
        $q = Doctrine_Query::create()
                ->from('SeguroBeneficio c')
                ->orderBy('c.nombre ASC');
        return $q;
    }
    
     public function postSave($values) {

        $seguro_beneficios = new SeguroBeneficio();
        $seguro_beneficios = $seguro_beneficios->getSeguroBeneficios();
        foreach ($seguro_beneficios as $seguro_beneficio) {
            sfContext::getInstance()->getUser()->setAttribute('seguros_beneficios' . $seguro_beneficio->getId(), $seguro_beneficio->getNombre());
        }
    }

}
