class TipoRecomendaciones extends BaseTipoRecomendaciones
{
    
    
     public function __toString() {
        return $this->getNombre();
    }
    
    
     
    public function getTipoRecomendaciones(){
        
         $q = Doctrine_Query::create()
            -> from('TipoRecomendaciones p')
            ->where('p.empresa_id=?', sfContext::getInstance()->getUser()->getAttribute('agente_user_empresa_id'));
            
       return $q;
        
    }
    
    public function postSave($values){

        $tipo_recomendaciones = new TipoRecomendaciones();
           $tipo_recomendaciones = $tipo_recomendaciones->getTipoRecomendaciones()->execute();
           foreach ($tipo_recomendaciones as $tipo_restricion){
               sfContext::getInstance()->getUser()->setAttribute('recomendaciones'.$tipo_restricion->getId(),$tipo_restricion->getNombre());
           }
          
    }
    
    
    
}
