class VehiculoPolitica extends BaseVehiculoPolitica
{
    
        public function __toString() {
        return $this->getNombre();
    }

    public function getVehiculoPoliticas() {
        $q = Doctrine_Query::create()
                ->from('VehiculoPolitica c');
        $q->orderBy('c.nombre ASC');
        return $q->execute();
    }
    
    static public $status = array(
        0 => 'Inactivo',
        1 => 'Activo',
    );
    
     public function postSave($values){
   
         $vehiculo_politicas = new VehiculoPolitica();
           $vehiculo_politicas = $vehiculo_politicas->getVehiculoPoliticas();
           foreach ($vehiculo_politicas as $vehiculo_politica){
               sfContext::getInstance()->getUser()->setAttribute('vehiculos_politicas'.$vehiculo_politica->getId(),$vehiculo_politica->getNombre());
           }
         
         
         
    }  
    
}

